<h4>Polaganja</h4>
<table>
    <tr>

    </tr>
    <tr *ngFor="let p of polaganja">
        <td>{{p.prijava.vrsta}}</td>
        <td>{{p.brojPoena}}</td>
        <td>{{p.datum}}</td>
        <td>{{p.prijava.mesto_naziv}}</td>
    </tr>
</table>
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Korisnik } from '../models/korisnik';
import { ServisService } from '../servis.service';
import { Prijava } from '../models/prijava';
import { Polaganje } from '../models/polaganje';

@Component({
  selector: 'app-kandidat',
  templateUrl: './kandidat.component.html',
  styleUrls: ['./kandidat.component.css']
})
export class KandidatComponent implements OnInit {

  constructor(private router:Router, private servis:ServisService) { }

  polaganja:Polaganje[] = []
  ulogovan:Korisnik
  ngOnInit() : void {
    this.ulogovan=JSON.parse(localStorage.getItem("ulogovan"))
    this.servis.sve_prijave_za_usera(this.ulogovan.username).subscribe(async (res:Prijava[])=>{
      if(res){
        for(let r of res){
          await this.servis.dohvati_polaganje(r.idPrijava).subscribe((ress:Polaganje)=>{
            if(ress){
              ress.prijava = r
              this.polaganja.push(ress)
            }
          })

        }
      }
    })
  }
  logout(){
    localStorage.removeItem("ulogovan")
    this.router.navigate([""])
  }

}
